$(document).ready(function () {
    svg4everybody({});

    // // popup video start
    $('[data-fancybox]').fancybox({
        youtube : {
            controls : 0,
            showinfo : 0
        },
        vimeo : {
            color : 'f00'
        }
    });
    
    const arr = $('#modalVideo source');
    let videoPath = [
            'static/video/mobile_video.mp4',
            'static/video/mobile_video.ogg',
            'static/video/mobile_video.webn'
        ];
        if ($(window).width() < 1024) {
            $.each(arr, function( key, el ) {
                $(el).attr('src', videoPath[key]);
                console.log( key, el)
              });
        }
    $(window).resize(function() {
        let videoPath = [
            'static/video/mobile_video.mp4',
            'static/video/mobile_video.ogg',
            'static/video/mobile_video.webn'
        ];
        if ($(window).width() < 1024) {
            $.each(arr, function( key, el ) {
                $(el).attr('src', videoPath[key]);
                console.log( key, el)
              });
        }
    });
    // // popup video end

    // функция кастомного input(type='file') 
    var fileInput  = document.querySelector( "#file" ),  
        button     = document.querySelector( ".reg-form__label--file" ),
        the_return = document.querySelector(".reg-form__file-return");
        
    button.addEventListener( "keydown", function( event ) {  
        if ( event.keyCode == 13 || event.keyCode == 32 ) {  
            fileInput.focus();  
        }  
    });
    button.addEventListener( "click", function( event ) {
        fileInput.focus();
        return false;
    });  
    fileInput.addEventListener( "change", function( event ) {  
        the_return.innerHTML = this.files[0].name;  
    }); 
    // функция кастомного input(type='file')  end

    // Валидация формы start
    validate();
    $('#inputName, #inputEmail, #file').change(validate);

    function validate(){
        if ($('#inputName').val().length   >   0   &&
            $('#inputEmail').val().length  >   0   &&
            $('#file').val().length    >   0) {
            $("#reg-btn").prop("disabled", false);
        }
        else {
            $("#reg-btn").prop("disabled", true);
        }
    }
    // Валидация формы start end

});

// инициализация анимации
new WOW().init({
    scrollContainer: '.scroll'
});
// инициализация анимации конец